mapper.insert:
INSERT INTO user ( id
            , email
            , password
            , name
            , age
) VALUES (
              #{id}
            , #{email}
            , #{password}
            , #{name}
            , #{age}
)

mapper.select:
SELECT id, password, email, name, age
FROM user
WHERE id = #{id}

SELECT COUNT(id)
FROM user
WHERE id = #{id} AND password = #{password}

mapper.update:
UPDATE user
SET 1=1
    , email = #{email}
    , password = #{password}
    , name = #{name}
    , age = #{age}
