mapper.insert:
INSERT INTO user ( username
            , email
            , password
            , name
            , age
) VALUES (
              #{username}
            , #{email}
            , #{password}
            , #{name}
            , #{age}
)

mapper.select:
SELECT username, password, email, name, age, role
FROM user
WHERE username = #{username}

SELECT username, password, role
FROM user
WHERE username = #{username}

mapper.update:
UPDATE user
SET 1=1
    , email = #{email}
    , password = #{password}
    , name = #{name}
    , age = #{age}
WHERE username = #{username}
